insert into `creature_text_locale` (`CreatureID`, `GroupID`, `ID`, `Locale`, `Text`) values('28070','26','0','enUS','I\'ll use the forge to make batches o\' earthen to stand guard... But our greatest challenge still remains: find and stop Loken!');
insert into `creature_text_locale` (`CreatureID`, `GroupID`, `ID`, `Locale`, `Text`) values('28070','25','0','enUS','Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul!');
insert into `creature_text_locale` (`CreatureID`, `GroupID`, `ID`, `Locale`, `Text`) values('28070','24','0','enUS','Ha, that did it! Help\'s a-comin\'! Take this, ya glowin\' iron brute!');
insert into `creature_text_locale` (`CreatureID`, `GroupID`, `ID`, `Locale`, `Text`) values('28070','23','0','enUS','What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it...');
