insert into `creature_text_locale` (`CreatureID`, `GroupID`, `ID`, `Locale`, `Text`) values('28070','20','0','enUS','You\'re right, I can come back to this later. I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in.');
insert into `creature_text_locale` (`CreatureID`, `GroupID`, `ID`, `Locale`, `Text`) values('28070','19','0','enUS','Hold everything! The Aesir and Vanir went to war? Why?');
