INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 0, 0, 'No. Grenja, !no te voy a comprar un tambor dorado! !Deja de pedírmelo o te quitaré ese sombrero nuevo!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 1, 0, '!Silencio, todoh! Tenemos visita.', 14, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 2, 0, '!Calmaoh todoh! Ahora oh saludará uno a uno, ¿verdad, camarada?', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 3, 0, '!Ja! Volni, !cómo ereh! Tal vez se haya vehtido a ohcurah... !Pero tieneh razón!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 4, 0, 'Sí, ya he oído lo que ha dicho, Man´zul. Pero sigo pensando que tu parche eh máh feo que el trasero de un saurolihco', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 5, 0, 'Grenja, eh nuestrah bardo residente. Te tocará cualquier canción que le pidah... siempre que tenga percusión. Le encanta golpear cosah. Tu cara no, ehpero. !Ja, ja! Que no, que no... bueno, igual sí.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 6, 0, 'El Paso del Hombre Muerto no tiene pérdida. Solo tieneh que dirigirte a lah rocah ohcurah y amenazanteh del noroehte. !Te vah a divertir!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 7, 0, '!A por elloh! Zulsan y yo te ehperaremoh en la posada.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 8, 0, 'It be safe to come out now?', 12, 0, 100, 1, 0, 0, 132696, 0, 'Rhan\'ka to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 9, 0, 'Grenja! Volni! Man\'zul! You be back! De gang\'s all here!', 12, 0, 100, 0, 0, 0, 147882, 0, 'Rhan\'ka to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 10, 0, 'You\'re right! It be time we brought $p into de gang officially. Prepare de ritual!', 12, 0, 100, 432, 0, 0, 134186, 0, 'Rhan\'ka to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 11, 0, 'He hervido la sangre y loh aguijoneroh y loh he mezclado en la copa ceremonial del Colmillo Dorado... Eh una piña. No te la comah. Solo tenemoh una.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 12, 0, 'Ah la sangre eh dulce... bébela y veráh lo mihmo que yo.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 13, 0, '!Te presento a tuh nuevo hermanoh y hermanah de la banda Colmillo Dorado!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 14, 0, 'Zuldazar noh traicionnó. Exiliadoh como forahteroh. Pero nunca noh traicionamoh entre nosotroh. Loh Colmillo Dorado conservamoh nuehtra lealtad.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 15, 0, 'Je, la primera vez siempre cuehta beber la sangre de ehcórpido del ritual. Échala. Ya te entrará mejor la próxima vez.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 16, 0, 'Te doy la bienvenida a la banda. Ahora tenemoh un objetivo común.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 17, 0, 'Preparaoh, !llegan huéhpedeh! !Qué emoción! Nada de matar, ¿eh?', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 18, 0, 'La Posada Colmillo Dorado, queda oficialmente inagurada!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (122723, 19, 0, 'Aqui siempre habrá un lugar pa ti. Seráh de la banda Colmillo Dorado de por vida.', 12, 0, 100, 1, 0, 0, 0, 0, '');
